import React, { useState } from 'react';
import './cart.css';
import Footer from '../footer/Footer';
import { food_list } from '../../assests/assets';

const Cart = ({ foodlist, itemCount, setItemCount }) => {
  const [discount, setDiscount] = useState(0); // State to store the discount percentage
  const [promoError, setPromoError] = useState(''); // State for promo code error messages

  const handleRemove = (itemId) => {
    setItemCount((prevCounts) => {
      let updatedCounts = { ...prevCounts };
      updatedCounts[itemId] = Math.max(0, (prevCounts[itemId] || 0) - 1);
      if (updatedCounts[itemId] === 0) {
        delete updatedCounts[itemId];
      }
      return updatedCounts;
    });
    console.log(itemCount);
  };

  const arr = [];

  const handlePromoSubmit = () => {
    const promoInput = document.getElementById('promo-code').value.trim();
    if (promoInput === 'Tomato50') {
      setDiscount(50); // Set 50% discount
      setPromoError(''); // Clear any error messages
    } else {
      setPromoError('Invalid promo code. Please try again.');
      setDiscount(0); // Reset discount
    }
  };

  const calculateSubtotal = () => {
    return arr.reduce((curr, total) => {
      return total = curr + total;
    }, 0);
  };

  const subtotal = calculateSubtotal();
  const deliveryFee = arr.length > 0 ? 48.5 : 0;
  const discountAmount = (subtotal * discount) / 100;
  const total = subtotal + deliveryFee - discountAmount;

  return (
    <>
      <div className='cart'>
        <div className='table-heading'>
          <p>Items</p>
          <p>Title</p>
          <p>Price</p>
          <p>Quantity</p>
          <p>Total</p>
          <p>Remove</p>
        </div>

        <div className='cart-items'>
          {Object.keys(itemCount).length > 0 ? (
            food_list.map((item, index) => {
              if (itemCount[index + 1] > 0) {
                return (
                  <div key={index} className='cart-item'>
                    <img src={item.image} alt="item.img" />
                    <h1 className='cart-item-name'>{item.name}</h1>
                    <p className='cart-item-price'>{item.price}</p>
                    <p className='cart-item-quantity'>{itemCount[index + 1]}</p>
                    <p className='cart-item-total'>{(itemCount[index + 1]) * (item.price)}</p>
                    <p onClick={() => handleRemove(item._id)} className='cross'>X</p>
                    <div className='arr'>
                      {arr.push((itemCount[index + 1]) * (item.price))}
                    </div>
                  </div>
                );
              }
            })
          ) : (
            <p className='no-items-cart'>There are no items in the cart</p>
          )}
        </div>

        <div className='cart-total-promos'>
          <div className='cart-total'>
            <h6>Cart Totals</h6>
            <div className='cart-subtotal'>
              <div className='total-info'>
                <p>Subtotal</p>
                <p>{subtotal.toFixed(2)}</p>
              </div>

              <div className='total-info'>
                <p>Delivery Fee</p>
                <p>{deliveryFee.toFixed(2)}</p>
              </div>

              {discount > 0 && (
                <div className='total-info'>
                  <p>Discount ({discount}%)</p>
                  <p>-{discountAmount.toFixed(2)}</p>
                </div>
              )}

              <div className='total-info' style={{ borderBottom: 'none', fontWeight: '600', fontSize: '15px' }}>
                <p>Total</p>
                <p>{total.toFixed(2)}</p>
              </div>
            </div>
            <div>
              <button>PROCEED TO CHECKOUT</button>
            </div>
          </div>
          <div className='cart-promos'>
            <p>If you have a promo code, enter it here:</p>
            <div className='promo-input'>
              <input type="text" id='promo-code' placeholder='Promo code' />
              <button onClick={handlePromoSubmit}>Submit</button>
            </div>
            {promoError && <p className='promo-error'>{promoError}</p>}
          </div>
        </div>
      </div>
      <Footer className='cart-footer' />
    </>
  );
};

export default Cart;
