import React, { useState } from 'react';
import { assets, food_list } from '../../assests/assets';
import './exploremenu.css';

const Exploremenu = ({ foodlist, itemCount, setItemCount, category }) => {
    const [similarCategory, setSimilarCategory] = useState('');
    const [showDetails, setShowDetails] = useState(false);
    const [desID, setDesID] = useState(null);

    const RestaurantNames = [
        { name: "Cheesecake Factory", address: "123 Main Street, Cityville, US" },
        { name: "Olive Garden", address: "456 Elm Street, Townsville, US" },
        { name: "PF Chang's", address: "789 Oak Avenue, Villageton, UK" },
        { name: "Hard Rock Cafe", address: "101 Rock Boulevard, Rockville, US" },
        { name: "Rainforest Cafe", address: "555 Jungle Way, Foresttown, US" },
        { name: "Applebee's", address: "777 Maple Drive, Appleville, US" },
        { name: "Buffalo Wild Wings", address: "888 Wing Street, Wingtown, US" },
        { name: "Chili's Grill & Bar", address: "999 Pepper Avenue, Chilitown, US" },
        { name: "Red Robin", address: "321 Robin Road, Robinville, US" },
    ];

    const handleAdd = (itemId) => {
        setItemCount((prevCounts) => ({
            ...prevCounts,
            [itemId]: (prevCounts[itemId] || 0) + 1,
        }));
    };

    const handleRemove = (itemId) => {
        setItemCount((prevCounts) => {
            const updatedCounts = { ...prevCounts };
            updatedCounts[itemId] = Math.max(0, (prevCounts[itemId] || 0) - 1);
            if (updatedCounts[itemId] === 0) {
                delete updatedCounts[itemId];
            }
            return updatedCounts;
        });
    };

    const handleShow = (id) => {
        setShowDetails(true);
        setDesID(id);
        setSimilarCategory(food_list[id].category);
    };

    return (
        <div className="food-list-container">
            <h1>Grab It Before It's Gone!</h1>
            <div className="food-items">
                {foodlist.map((item) => (
                    <div key={item._id} className="food-item">
                        <div className="food-item-img-container">
                            <img
                                src={item.image}
                                alt=""
                                className="food-item-img"
                                onClick={() => handleShow(item._id - 1)}
                            />
                            {itemCount[item._id] > 0 ? (
                                <div className="add-remove-icon">
                                    <img
                                        src={assets.remove_icon_red}
                                        alt=""
                                        className="remove-icon-red"
                                        onClick={() => handleRemove(item._id)}
                                    />
                                    <p>{itemCount[item._id] || 0}</p>
                                    <img
                                        src={assets.add_icon_green}
                                        alt=""
                                        className="add-icon-green"
                                        onClick={() => handleAdd(item._id)}
                                    />
                                </div>
                            ) : (
                                <img
                                    src={assets.add_icon_white}
                                    alt=""
                                    className="add-icon-white"
                                    onClick={() => handleAdd(item._id)}
                                />
                            )}
                        </div>
                        <div
                            className="food-item-info"
                            onClick={() => handleShow(item._id - 1)}
                        >
                            <div className="food-item-name-ratings">
                                <h4>{item.name}</h4>
                                <img
                                    src={assets.rating_starts}
                                    alt=""
                                    className="star-rating"
                                />
                            </div>
                            <p className="food-item-des">{item.description}</p>
                            <p className="food-item-price">{"â‚¹ " + item.price}</p>
                        </div>
                    </div>
                ))}
            </div>

            {showDetails && (
                <div className="description">
                    <div className="des-container">
                        <div className="des-info-container">
                            <div className="des-img-container">
                                <img src={food_list[desID].image} alt="" />
                            </div>
                            <div className="des-info">
                                <h1>{RestaurantNames[Math.floor(Math.random() * 9)].name}</h1>
                                <h6>{RestaurantNames[Math.floor(Math.random() * 9)].address}</h6>
                                <h2 className="food-title">{food_list[desID].name}</h2>
                                <p>
                                    Lorem ipsum dolor sit amet, consectetur adipisicing elit. Ratione
                                    nesciunt ipsam facere fuga eos provident voluptatum repellat.
                                </p>
                                <button className="Order-btn">Order Now</button>
                            </div>
                            <img
                                src={assets.cross_icon}
                                alt=""
                                onClick={() => setShowDetails(false)}
                                className="cross-icon"
                            />
                        </div>

                        <div className="similar-products">
                            {foodlist
                                .filter(
                                    (item) =>
                                        item.category === similarCategory && item !== food_list[desID]
                                )
                                .map((item) => (
                                    <div
                                        key={item._id}
                                        className="similar-food-item"
                                        onClick={() => handleShow(item._id - 1)}
                                    >
                                        <div className="similar-food-item-img-container">
                                            <img
                                                src={item.image}
                                                alt=""
                                                className="similar-food-item-img"
                                            />
                                        </div>
                                        <div className="similar-food-item-info">
                                            <div className="similar-food-item-name-ratings">
                                                <h4>{item.name}</h4>
                                                <img
                                                    src={assets.rating_starts}
                                                    alt=""
                                                    className="similar-star-rating"
                                                />
                                            </div>
                                            <p className="similar-food-item-price">
                                                {"â‚¹ " + item.price}
                                            </p>
                                        </div>
                                    </div>
                                ))}
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};

export default Exploremenu;
